import React, { useState } from 'react'
import {Link
} from "react-router-dom";

import '../pages/quiz.css'
import { QuizBank } from './quizBank'
import Logo from '../../assets/images/Logo.jpg'
import Home from '../../assets/images/home.png'

const Quiz = () => {

  var quizBank = QuizBank;

  // console.log('quizBank', quizBank);

  const [showResults, setShowResults] = useState(false)
  // console.log('showResults', showResults);
  const onClick = () => setShowResults(true)
  const [questionValue, setQuestionValue] = useState({
    rating: []
  })
  const handleChange = (e) => {
    if (e.target.type === "radio") {
      // console.log(e);
      const { value } = e.target
      let selected = questionValue.rating
      let find = selected.findIndex(item => item === value)
      // console.log(find);
      if (find > -1) {
        selected.splice(find, 1)
      }
      else {
        selected.push(value)
      }
      setQuestionValue(prevstate => ({
        ...prevstate, rating: selected
      }
      ))
    }
    else {
      const { name, value } = e.target
      setQuestionValue(prevstate => (
        {
          ...prevstate, [name]: value
        }
      ))
    }
  }
  console.log('questionValue', questionValue);

  return (
    <>
      <section>
        <div className='container'>
          <div className='row'>
            <div className='col ms-10px'>
              <img src={Logo} className='w-25' alt="..." />
              <Link to='#'>HOME</Link>
              <Link to='#'>FOR YOU</Link>
              <Link to='#'>FOR YOUR BUSINESS</Link>
              <Link to='#'>CARBON ABSORBING</Link>
              <Link to='#'>START YOUR AUDIT</Link>

              <button className='btn' type='btn'> FIND OUT MORE</button>
            </div>
          </div>

          <div className='row'>
            <div className='col'>
              <img src={Home} className='position-absolute top-50 start-50' alt='home' />

              {quizBank.map((index, key) => {

                return (
                  <ul key={key}>
                    {index.ABOUT_YOU && index.ABOUT_YOU.map((question, key) => {
                      console.log('questionsssssss', question);
                      return (
                        <li key={key}>

                          <span className='fs-3 '> {question.question}</span>
                          {question.options.map((answers, key) => {
                            return (
                              <ul key={key}>
                                <li> <input className="form-check-input mx-1" type='radio' value={answers.rating} onClick={onClick} onChange={handleChange} />{answers.label} </li></ul>
                            )
                          })}
                        </li>
                      )

                    })
                    }
                    {index.YOUR_HOME && index.YOUR_HOME.map((question, key) => {
                      return (

                        <ul key={key}> {showResults ? <span className='fs-3'>{question.question}</span> : null}

                          <li>{question.options.map((option, key) => {
                            return (
                              <ul key={key}> {showResults ?
                                <li ><input className="form-check-input mx-1" type='radio' value={option.rating} onClick={onClick} />{option.label}</li> : null}
                              </ul>
                            )
                          })}</li>
                        </ul>
                      )
                    })}
                  </ul>
                )
              })}
            </div>
          </div>
        </div>
      </section>
    </>
  )
}
export default Quiz;
