import React, { useState } from 'react'
import '../pages/quiz.css'
import { QuizBank } from './quizBank'
import Logo from '../../assets/images/Logo.jpg'
import Home from '../../assets/images/home.png'

const Quiz = () => {

  var quizBank = QuizBank;
  const [showResults, setShowResults] = useState(false)
  const onClick = () => setShowResults(true)

  const [questionValue, setQuestionValue] = useState({
    rating: []
  })
  const handleChange = (e) => {
    const { value } = e.target

    let selected = questionValue.rating

    selected.push(value)

    setQuestionValue(prevstate => (
      {
        ...prevstate
      }

    ))
    const initialValue = 0;
    const sumarr = selected.reduce((a, b) =>
      a + b,
      initialValue
    )
    console.log('sumarr', sumarr);
  }
  console.log('questionValue', questionValue);

  return (
    <>
      <section>
        <div className='container'>
          <div className='row'>
            <div className='col ms-10px'>
              <img src={Logo} className='w-25' alt="..." />
              <a href='#'>HOME</a>
              <a href='#'>FOR YOU</a>
              <a href='#'>FOR YOUR BUSINESS</a>
              <a href='#'>CARBON ABSORBING</a>
              <a href='#'>START YOUR AUDIT</a>

              <button className='btn' type='btn'> FIND OUT MORE</button>
            </div>
          </div>

          <div className='row'>
            <div className='col'>
              <img src={Home} className='position-absolute top-50 start-50' alt='home' />

              {quizBank.map((index, key) => {

                return (
                  <ul key={key}>

                    {index.ABOUT_YOU && index.ABOUT_YOU.map((question, key) => {

                      return (

                        <>
                        <li key={key}>
                          <span className='fs-3 '> {question.question}</span>
                          {question.options.map((answers, key) => {
                            return (
                              <li key={key}>
                                <li>
                                  <input className="form-check-input mx-1" type='radio' value={answers.rating} name="flexRadioDefault"
                                    onClick={onClick} onChange={handleChange} />{answers.label}
                                </li>
                              </li>
                            )
                          })}
                        </li>
                        </>

                      )

                    })
                    }
                    {index.YOUR_HOME && index.YOUR_HOME.map((question, key) => {
                      return (

                        <li key={key}> {showResults ? <span className='fs-3'>{question.question}</span> : null}

                          <li>{question.options.map((option, key) => {
                            return (
                              <li key={key}> {showResults ?
                                <li ><input className="form-check-input mx-1" type='radio' value={option.rating}
                                  name="flexRadioDefault"
                                  onClick={onClick} onChange={handleChange} />{option.label}</li> : null}
                              </li>
                            )
                          })}</li>
                        </li>
                      )
                    })}


                  </ul>
                )
              })}
            </div>
          </div>
        </div>
      </section>
    </>
  )
}
export default Quiz;
